INSERT INTO `ae_calculation_analysis` VALUES ('32', '2', '<p><strong><span style=\"font-size:14px\">2</span></strong><strong><span style=\"font-size:14px;font-family:宋体\">、甲公司为上市公司，为了提高闲置资金的使用率，甲公司利用其多余资金进行投资，具体情况如下：</span></strong><strong><span style=\"font-size:14px\"><br/> </span></strong><strong><span style=\"font-size:14px;font-family:宋体\">（</span></strong><strong><span style=\"font-size:14px\">1</span></strong><strong><span style=\"font-size:14px;font-family:宋体\">）</span></strong><strong><span style=\"font-size:14px\">2012</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">年</span></strong><strong><span style=\"font-size:14px\">5</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">月</span></strong><strong><span style=\"font-size:14px\">20</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">日，甲公司从深圳证券交易所购入乙公司股票</span></strong><strong><span style=\"font-size:14px\">100</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">万股，占乙公司有表决权股份的</span></strong><strong><span style=\"font-size:14px\">5%</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">，支付价款合计</span></strong><strong><span style=\"font-size:14px\">508</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">万元，其中证券交易税费等交易费用</span></strong><strong><span style=\"font-size:14px\">0.8</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">万元，已宣告但尚未发放的现金股利为</span></strong><strong><span style=\"font-size:14px\">7.2</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">万元。甲公司将其划分为可供出售金融资产。</span></strong><strong><span style=\"font-size:14px\"><br/> </span></strong><strong><span style=\"font-size:14px;font-family:宋体\">（</span></strong><strong><span style=\"font-size:14px\">2</span></strong><strong><span style=\"font-size:14px;font-family:宋体\">）</span></strong><strong><span style=\"font-size:14px\">2012</span></strong><strong><span style=\"font-size:14px;font-family: 宋体\">年</span></s