INSERT INTO `ae_judgment` VALUES ('1', '企业进行在建工程预付的工程价款，应在“预付账款”科目核算。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('2', '企业无法可靠区分研究阶段和开发阶段支出的，应将其所发生的研发支出全部资本化计入无形资产成本。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('3', '企业将某项房地产部分用于出租、部分自用，且出租部分能单独计量和出售，企业应将该项房地产整体确认为投资性房地产。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('4', '投资性房地产从成本计量模式转为公允价值计量模式的，应当作为会计政策变更处理，将计量模式变更时公允价值与账面价值之间的差额，调整当期损益。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('5', '长期股权投资采用权益法核算的，在持股比例不变的情况下，被投资单位发生的除净损益、其他综合收益及利润分配以外的所有者权益的其他变动，企业按照持股比例应享有的份额，借记或贷记“资本公积——资本溢价（股本溢价）”。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('6', '权益法下，被投资单位所持有的可供出售金融资产公允价值发生变动，投资方不做处理。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('7', '企业取得可供出售金融资产支付的价款中包含已宣告但尚未发放的现金股利或已到付息期但尚未领取的债券利息，应当单独确认为应收项目，不构成可供出售金融资产的初始入账金额。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('8', '处置可供出售金融资产时，应将取得的价款与该金融资产账面价值之间的差额，计入投资损益；原直接计入所有者权益的公允价值变动累计额不需要进行会计处理。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('9', '与固定资产有关的经济利益预期实现方式有重大改变的，应当改变固定资产折旧方法，并作为会计估计变更进行会计处理。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('10', '已达到预定可使用状态但尚未办理竣工决算的固定资产不应计提折旧。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('11', '企业购入不需要安装的生产设备，购买价款超过正常信用条件延期支付，实质具有融资性质的，应当以购买价款的现值为基础确定其成本。（ ） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('12', '企业取得长期股权投资，实际支付的价款中包含已宣告但尚未发放的现金股利或利润，应该计入长期股权投资的成本。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('13', '企业以经营租赁方式租入的固定资产发生的改良支出，应记入“管理费用”科目。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('14', '企业采用计划成本进行材料日常核算时，月末分摊材料成本差异时，超支差异计入“材料成本差异”科目的借方，节约差异计入“材料成本差异”的贷方。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('15', '专门用于生产某产品的无形资产，其所包含的经济利益通过所生产的产品实现的，该无形资产的摊销额应计入产品成本。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('16', '企业转让采用公允价值模式计量的投资性房地产时，如果存在原转换日计入其他综合收益的金额，也一并结转，贷记“其他业务收入”科目。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('17', '企业持有的可供出售金融资产如果发生减值，以后价值回升，之前计提的减值不能转回。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('18', '交易性金融资产处置时，只需要将公允价值变动损益当期的发生额转入投资收益科目即可。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('19', '采用月末一次加权平均法的只在月末一次计算加权平均单价，比较简单，有利于简化成本计算工作。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('20', '商品流通企业在采购商品过程中发生的运杂费等进货费用，应当计入存货采购成本。进货费用数额较小的，也可以在发生时直接计入当期费用。（　） ', '2', '1', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('21', '处置长期股权投资时，不同时结转已计提的长期股权投资减值准备，待期末一并调整。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('22', '企业对包装物采用计划成本核算，对于随同商品出售而不单独计价的包装物，企业应在发出该包装物时，按计划成本计入销售费用。（　） ', '2', '0', '5', '0', '13', '1487674348', '0', '1');
INSERT INTO `ae_judgment` VALUES ('23', '预收账款不多的企业，可以不设置“预收账款”科目。企业预收客户货款时，直接将其记入“应收账款”科目的借方。（　）', '2', '0', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('24', '经股东大会批准，企业对外宣告分配现金股利时，应确认一项负债。（　）', '2', '1', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('25', '暂收个人的款项和经营租入固定资产的未付租金应通过“其他应付款”科目核算。（　）', '2', '1', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('26', '企业购进的货物发生非常损失，以及将购进货物改变用途的（如用于非应税项目、集体福利或个人消费等），其进项税额应通过“应交税费—应交增值税（进项税额转出）”科目核算。（　）', '2', '1', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('27', '企业进口应税物资在进口环节应交的消费税，应计入“应交税费——应交消费税”科目中。（　）', '2', '0', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('28', '企业按规定计算的代扣代缴的职工个人所得税，借记“管理费用”等科目，贷记“其他应付款”科目。（　）', '2', '0', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('29', '企业按面值发行债券时，借记“银行存款”科目，贷记“应付债券——面值”科目，存在差额的，应记入“投资收益”科目。（　）', '2', '0', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('30', '增值税小规模纳税人购进货物支付的增值税直接计入有关货物的成本。（ ）', '2', '1', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('31', '企业购入货物验收入库后，若货款尚未支付，发票账单尚未收到，应在月末按照估计的金额确认一笔负债，反映在资产负债表有关负债项目中。（　）', '2', '1', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('32', '企业的出口产品按照规定退税的，按照应收的退税额计入企业的“营业外收入”科目中。（　）', '2', '0', '5', '0', '14', '1489834404', '0', '1');
INSERT INTO `ae_judgment` VALUES ('33', '企业发行股票支付的手续费，如果是溢价发行股票的，应从溢价中抵扣，冲减资本公积（股本溢价），无溢价或溢价金额不足以抵扣的，应将不足抵扣的部分冲减盈余公积和未分配利润。（　）', '2', '1', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('34', '企业清算时应先返还所有者的所有者权益，再清偿企业的负债。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('35', '收入能够导致企业所有者权益增加，但导致所有者权益增加的不一定都是收入。（　）', '2', '1', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('36', '企业减少实收资本应按法定程序报经批准，股份有限公司采用收购本公司股票方式减资的，按股票面值和注销股数计算的股票面值总额冲减股本，按注销库存股的账面余额与所冲减股本差额冲减盈余公积。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('37', '无论是以税前利润还是以税后利润弥补亏损，在会计上都无需做专门的会计分录，所不同的只是两者计算交纳所得税时的处理不同而已。（　）', '2', '1', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('38', '不管企业期初是否存在未弥补的亏损，当期计提法定盈余公积的基数都是当期实现的净利润。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('39', '企业计提法定盈余公积的基数是当年实现的净利润和企业年初未分配利润之和。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('40', '实收资本的构成比例或股东的股份比例，是确定所有者在企业所有者权益中份额的基础，但不是企业进行利润或股利分配的依据。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('41', '留存收益包括资本公积和未分配利润。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('42', '企业以盈余公积向投资者分配现金股利，不会引起留存收益总额的变动。（　）', '2', '0', '5', '0', '15', '1490176428', '0', '1');
INSERT INTO `ae_judgment` VALUES ('43', '企业发生的销售退回，均应在发生时冲减发生当期的销售商品收入，同时冲减当期销售商品成本以及税金。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('44', '企业对外提供劳务，所实现的收入通过其他业务收入核算，结转的相关成本通过其他业务成本核算。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('45', '企业对于在同一会计期间内能够一次完成的劳务，应分期采用完工百分比法确认收入和结转成本。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('46', '企业购买商品附有商业折扣的，如果商业折扣实际发生了，则应按扣除商业折扣后的含税金额作为应付账款的入账价值。（　）', '2', '1', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('47', '发出的商品不符合收入确认条件，但如果销售该商品的纳税义务已经发生，企业应当确认应交的增值税销项税额，借记“应收账款”，贷记“应交税费——应交增值税（销项税额）”。（　）', '2', '1', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('48', '企业采用支付手续费方式委托代销商品，委托方确认商品销售收入的时间是发出商品时。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('49', '预收款销售方式下，销售方直到收到最后一笔款项时才将商品交付给购货方，在此之前按照预收的货款确认相等金额的收入。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('50', '企业销售商品确认收入后，对于客户实际享受的现金折扣应当确认为当期财务费用。（　）', '2', '1', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('51', '企业的收入包括主营业务收入、其他业务收入和营业外收入。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('52', '企业出售原材料取得的款项扣除其成本及相关费用后的净额，应当记入“营业外收入”或“营业外支出”。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('53', '以销售商品、提供劳务为主营业务的企业，销售单独计价的包装物实现的收入通过“主营业务收入”科目进行核算。（　）', '2', '0', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('54', '甲企业2011年12月向乙企业提供某专利的使用权。合同规定：使用期10年，一次性收取使用费360 000元，不提供后续服务，款项已经收到。则甲企业当年应确认的使用费收入为360 000元。（　）', '2', '1', '5', '0', '16', '1490235098', '0', '1');
INSERT INTO `ae_judgment` VALUES ('55', '应当在确认销售商品收入、提供劳务收入等时，将已销售商品、已提供劳务的成本等计入当期损益。（　）', '2', '1', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('56', '收回出租包装物因不能使用而报废的残料价值，应通过“销售费用”科目核算。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('57', '工业企业利润表中“营业税金及附加”项目不包括增值税和土地增值税。（　）', '2', '1', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('58', '企业出售固定资产应交的营业税，应列入利润表的“营业税金及附加”项目。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('59', '某企业2014年年初有上年形成的亏损25万元，当年实现利润总额15万元，所得税税率为25％。假设以前年度没有亏损，则企业2014年不需要交纳企业所得税。（　）', '2', '1', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('60', '“营业税金及附加”科目核算的内容不包括印花税、契税、增值税和城市维护建设税。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('61', '企业筹建期间的借款费用、资本化的借款利息支出、销售商品发生的商业折扣和支付的银行承兑汇票手续费都通过财务费用科目核算。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('62', '企业向银行或其他金融机构借入的各种款项，所发生的利息均应计入“财务费用”。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('63', '企业生产经营期间的长期借款利息支出应该全部计入财务费用中。（　）', '2', '0', '5', '0', '17', '1490237340', '0', '1');
INSERT INTO `ae_judgment` VALUES ('64', '管理费用、资产减值损失、营业税金及附加和营业外收入都会影响企业的营业利润。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('65', '甲企业本期主营业务收入为500万元，主营业务成本为300万元，其他业务收入为200万元，其他业务成本为100万元，销售费用为15万元，资产减值损失为45万元，公允价值变动收益为60万元，投资收益为20万元，营业外收入为50万元，所得税费用为95万元，假定不考虑其他因素，该企业本期利润总额为370万元。（　）', '2', '1', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('66', '某企业完成政府下达技能培训任务，收到财政补助资金150万元，所以该企业应当确认的递延收益为150万元。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('67', '企业接受非现金资产捐赠，应该计入实收资本中。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('68', '企业获得的捐赠利得应该计入营业外收入中，影响利润总额。（　）', '2', '1', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('69', '某企业年初有上年形成的亏损50万元，当年实现利润总额40万元。假设企业本期无纳税调整事项，则企业当年还应交纳一定的企业所得税。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('70', '某企业年初有上年形成的亏损25万元，当年实现利润总额30万元。假设企业本期无纳税调整事项，则企业当年还应缴纳一定的企业所得税。（　）', '2', '1', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('71', '表结法在各月均可通过“本年利润”科目提供当月及本年累计的利润（或亏损）额，但增加了转账环节和工作量。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('72', '账结法下，企业的营业外收入科目在期末可以有余额。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('73', '年度终了，企业应将“本年利润”科目的本年累计余额转入“利润分配——未分配利润”科目，此时本年利润科目的本年累计余额即为净利润的金额。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('74', '年度终了，只有在企业盈利的情况下，才将“本年利润”科目的本年累计余额转入“利润分配——未分配利润”科目。（　）', '2', '0', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('75', '年度终了，应将“本年利润”科目的本年累计余额转入“利润分配——未分配利润”科目。（　）', '2', '1', '5', '0', '18', '1490254218', '0', '1');
INSERT INTO `ae_judgment` VALUES ('76', '企业采用计划成本法核算原材料时，如果当期的材料成本差异率为负数，那么反映在资产负债表中的原材料的实际成本大于计划成本。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('77', '企业在编制现金流量表时，对企业为职工支付的住房公积金、为职工缴纳的商业保险金、社会保障基金等，应按照职工的工作性质和服务对象分别在经营活动和投资活动产生的现金流量有关项目中反映。（　）', '2', '1', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('78', '直接法，一般是以利润表中的营业收入为起算点，调节与经营活动有关的增减变动，然后计算出经营活动产生的现金流量。（　）', '2', '1', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('79', '利润表是反映企业在一定会计期间财务状况的报表。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('80', '“利润分配”总账的年末余额不一定与相应的资产负债表中“未分配利润”项目的数额一致。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('81', '资产负债表中的“无形资产”项目是根据“研发支出”科目中所属的资本化支出明细科目的期末余额填列的。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('82', '“长期股权投资”项目应根据“长期股权投资”科目的期末余额，减去“长期股权投资减值准备”科目的期末余额后的金额填列。（　）', '2', '1', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('83', '企业编制财务报表的时候如果没有需要可以不编制报表附注。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('84', '所有者权益，是指企业资产扣除负债后的剩余权益，反映企业某一会计期间股东（投资者）拥有的净资产的总额。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('85', '支付的在建工程人员薪酬属于企业现金流量表“经营活动产生的现金流量”项目。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('86', '企业经营租赁支付的租金、支付的差旅费、业务招待费、保险费、罚款支出等其他的与经营活动有关的现金流出，应填列在“支付其他与经营活动有关的现金”。（　）', '2', '1', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('87', '现金流量表中“销售商品、提供劳务收到的现金”项目，反映本企业自营销售商品或提供劳务收到的现金，不包括委托代销商品收到的现金。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('88', '处置固定资产产生现金流入，应该属于经营活动的现金流量。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('89', '所有者权益变动表中的上年金额栏应该根据上年度所有者权益变动表“本年金额”栏内所列数字进行填列，上年项目的名称和内容与本年不一致的，应按照上年的名称和数字对本年进行调整。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('90', '“利润分配”总账的年末余额不一定与相应的资产负债表中未分配利润项目的数额一致。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('91', '某企业2013年1月利润总额100万元，确认了所得税费用25万元，则在表结法下本年利润账户的月末余额为75万元。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('92', '企业出售无形资产形成的净损失，应列入利润表的“营业外支出”项目，使得企业的营业利润增加。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('93', '“应付债券”项目应该根据“应付债券”总账科目余额填列。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('94', '“开发支出”项目应当根据“研发支出”科目中所属的“资本化支出”明细科目期末余额填列。（　）', '2', '1', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('95', '财务报表提供的信息仅供外部的投资者和债权人使用。（　）', '2', '0', '5', '0', '19', '1490261931', '0', '1');
INSERT INTO `ae_judgment` VALUES ('96', '应计入产品成本，但不能分清应由何种产品负担的费用，应计入生产成本。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('97', '采用直接分配法分配辅助生产费用时，应考虑各辅助生产车间相互提供产品或劳务的情况。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('98', '辅助生产内部相互提供产品和劳务不多、不进行费用的交互分配、对辅助生产成本和企业产品成本影响不大的情况下，分配辅助生产费用时，适合采用计划成本法。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('99', '当期末在产品数量很少时，对生产成本在完工产品和在产品之间进行分配时，仍然要考虑期末在产品应负担的生产成本。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('100', '采用约当产量比例法核算时，计算分配原材料费用与分配加工费用所用的分配率都是一致的。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('101', '采用定额比例法分配产品的生产成本，直接人工等加工成本，可以按各定额成本的比例分配，也可按定额工时比例分配。（　）', '2', '1', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('102', '联产品，是指使用同种原料，经过同一生产过程，在生产主产品的同时附带生产出的非主要产品。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('103', '副产品，是指在同一生产过程中，使用同种原料，在生产主产品的同时附带生产出来的非主要产品。（　）', '2', '1', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('104', '可修复废品损失返修以前发生的生产费用，也属于废品损失，需要计算废品损失的成本。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('105', '不可修复废品损失的生产成本，可按废品所耗实际费用计算，也可按废品所耗定额费用计算。（　）', '2', '1', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('106', '“基本生产成本”科目的月末余额，表示基本生产车间在产品的成本。（　）', '2', '1', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('107', '辅助生产成本是为基本生产服务而进行的产品生产和劳务供应。该科目按辅助生产车间和提供的产品、劳务分为辅助生产成本明细账，按辅助生产的成本项目分设专栏。期末，对共同负担的费用按照一定的分配标准分配给各受益对象。（　）', '2', '1', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('108', '在产品有广义和狭义之分，狭义的在产品是指最终没有完成最终步骤的产品；广义在产品只包括该车间或该生产步骤加工中的产品。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('109', '产品成本是由费用构成的，因此企业发生的费用就是产品成本。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('110', '成本一般以生产过程中取得的各种原始凭证为计算依据。（　）', '2', '0', '5', '0', '20', '1490320015', '0', '1');
INSERT INTO `ae_judgment` VALUES ('111', '简化的分批法下，各项累计间接费用分配率，既是在各批完工产品之间分配费用的依据，也是在完工批别与月末在产品批别之间，以及某批产品的完工产品与月末在产品之间分配该费用的依据。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('112', '采用平行结转分步法，每一生产步骤的生产成本要在最终完工产品与本步骤尚未加工完成的在产品之间进行分配。（　）', '2', '0', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('113', '产品单位成本变动会使可比产品成本降低额变动。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('114', '在产品品种比重和产品单位成本不变的情况下，产量增减会使成本降低额和降低率同时发生变动。（　）', '2', '0', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('115', '成本计算对象的确定，是正确计算产品成本的前提，也是区别各种成本计算方法的主要标志。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('116', '品种法下计算产品成本一般是不定期的，可以是月末，也可以是年末。（　）', '2', '0', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('117', '采用分批法计算产品成本时，成本计算期与产品生产周期、财务报告期基本一致。（　）', '2', '0', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('118', '产品生产成本表是反映企业在报告期内生产的全部产品的总成本的报表。该表一般分为两种，一种按成本项目反映，另一种按产品种类反映。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('119', '对制造费用进行分析，由于制造费用的项目很多，分析时应选择超支或节约数额较大或者成本比重较大的项目有重点地进行。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('120', '同一车间不同的产品可以同时采用不同的成本计算方法，甚至同一产品的不同步骤也可以采用不同的成本计算方法。（　）', '2', '1', '5', '0', '21', '1490325225', '0', '1');
INSERT INTO `ae_judgment` VALUES ('121', '根据《行政复议法》的规定，因不可抗力或者其他正当理由耽误行政复议法定申请期限的，申请期限自障碍消除之日起继续计算。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('122', '当事人对仲裁协议的效力有异议的，一方请求人民法院作出裁定，另一方请求仲裁委员会作出决定的，由人民法院裁定。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('123', '企业与税务机关就税收担保财产发生的纠纷，因为是属于财产权益的纠纷，因此是可以提请仲裁的。（　　）', '2', '0', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('124', '行政复议机关责令被申请人重新作出具体行政行为的，被申请人不得以同一的事实和理由作出与原具体行政行为相同或者基本相同的具体行政行为。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('125', '行政法调整的是行政机关与行政管理的相对人之间因民事活动而发生的法律关系。（　　）', '2', '0', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('126', '法院审理民事或行政案件，全部应当公开进行。（　　）', '2', '0', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('127', '诉讼采取的是两审终审制度，二审法院的判决、裁定是终审的判决、裁定。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('128', '对于发生法律效力的判决、裁定，由第一审法院执行；对于调解书、仲裁机构的生效裁决、公证机关依法赋予强制执行效力的债权文书等，则由被执行人住所地或者被执行的财产所在地法院执行。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('129', '平等民事主体的当事人之间发生的经济纠纷，可以同时选择仲裁和民事诉讼。（　　）', '2', '0', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('130', '拘役，是对违反治安管理的人，依法在短期内限制其人身自由的处罚。（　　）', '2', '0', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('131', '全国人民代表大会及其常务委员会有权就有关问题作出规范性决议或决定，它们与法律具有同等地位和效力。（　　）', '2', '1', '4', '0', '23', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('132', '竞业限制条款适用范围仅限于用人单位的高级管理人员、高级技术人员，对其他人没有约束力。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('133', '根据规定，当事人申请劳动争议调解必须提出书面申请。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('134', '劳动合同既涉及财产关系，也涉及人身关系，而一般的合同仅涉及财产关系。（　　）', '2', '1', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('135', '甲公司招用王某时，要求其缴纳600元的工作服押金，甲公司的做法不符合法律规定。（　　）', '2', '1', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('136', '根据劳动合同法律制度的规定，劳动者在本单位连续工作满10年，且距法定退休年龄不足10年的，用人单位不得解除劳动合同。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('137', '根据规定，劳动仲裁申请人必须提交书面仲裁申请，并按照被申请人人数提交副本。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('138', '根据劳动合同法律制度的规定，医疗期是职工工作遭受事故伤害或者患职业病需要暂停工作、接受工伤医疗的期间。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('139', '对劳动合同的无效有争议的，只能由劳动争议仲裁机构进行确认。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('140', '根据劳动合同法律制度的规定，服务期与劳动合同一般期限在时间长度上不一致，前者一般短于后者。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('141', '劳动合同部分无效的，该劳动合同从订立时起就没有法律约束力。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('142', '集体合同订立后，应当报送劳动行政部门，劳动行政部门自收到集体合同文本之日起15日内审查同意，集体合同生效。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('143', '事业单位与实行聘用制的工作人员订立、履行、变更、解除或者终止劳动合同，依照劳动合同法有关规定执行。（　　）', '2', '0', '4', '0', '24', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('144', '票据是“金钱债权证券”，即票据上体现的权利性质是财产权而不是其他权利，财产权的内容是请求支付一定的金钱和物品。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('145', '网银中心根据签约柜台核实后的邮件（传真件），进行申请的初审和复审。（　　）', '2', '1', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('146', '存款人因向银行借款而需要开立一般存款账户的，除常规的证明文件外，还应向银行出具借款合同。（　　）', '2', '1', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('147', '银行本票可以用于转账，注明“现金”字样的银行本票可以用于支取现金。（　　）', '2', '1', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('148', '银行汇票是银行签发、银行承兑、银行付款的票据。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('149', '单位人民币卡账户的资金一律从其基本存款账户转账存入，不得存取现金，不得将销货收入存入单位卡账户。（　　）', '2', '1', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('150', '特约商户，是指与收单机构签订银行卡受理协议、按约定受理银行卡并委托收单机构为其完成交易资金结算的企事业单位、个体工商户或其他组织，以及按照囯家工商管理机关有关规定，开展网络商品交易等经营活动的企业法人。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('151', '中国银联股份有限公司，由中国工商银行、中国人民银行、中国建设银行、中国农业银行和交通银行等银行卡发卡金融机构共同发起成立。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('152', '某公司成立时注册验资的临时存款账户转为基本存款账户的，应在正式转换账户之日起3个工作日后，方可使用基本存款账户办理付款业务。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('153', '互联网支付企业仅能够在线支付，不具有供担保功能。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('154', '保证人未在票据或者粘单上记载“保证”字样，而另行签订保证合同或者保证条款的，也属于票据保证。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('155', '临时存款账户既可用于办理临时机构以及存款人临时经营活动发生的资金收付，也可用于办理其日常经营活动发生的资金收付。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('156', '在使用信用证结算中，申请人交存的保证金和其存款账户余额不足支付的，开证行可以暂停支付款项。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('157', '根据规定，单位和个人签发空头支票或者签发与其预留的签章不符的支票，不以骗取财物为目的，由中国人民银行处以票面金额5%但不低于2000元的罚款；持票人有权要求出票人赔偿支票金额2%的赔偿金。（　　）', '2', '0', '4', '0', '25', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('158', '根据规定，一般贸易下进口货物的关税完税价格以海关审定的成交价格为基础的到岸价格作为完税价格。（　　）', '2', '1', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('159', '纳税人用于换取生产资料和消费资料、投资入股和抵偿债务等方面的应税消费品，应当以纳税人同类应税消费品的平均销售价格作为计税依据计算消费税。（　　）', '2', '0', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('160', '纳税人取得符合规定的发票，应自开具之日起90天内到税务机关办理认证，并于认证通过的次月申报期内抵扣。（　）', '2', '0', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('161', '小规模纳税人实行简易征税办法，并且一般不使用增值税专用发票，但可以到税务机关代开增值税专用发票。（　　）', '2', '1', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('162', '纳税人出口货物，适用零税率，但是，国务院另有规定的除外。（　　）', '2', '1', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('163', '残疾人个人提供营改增应税服务免征增值税。（　　）', '2', '1', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('164', '购进中型商务客车改装生产的汽车不征收消费税。（　）', '2', '0', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('165', '自2015年5月10日起，将卷烟批发环节从价税税率由5%提高至11%，并按0.003元/支加征从量税。（　　）', '2', '0', '4', '0', '26', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('166', '企业所得税纳税人用于公益性捐赠支出的扣除比例，不得超过年度应纳税所得额的12%。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('167', '企业开发新技术、新产品、新工艺发生的研究开发费用，可以在计算应纳税所得额时除按照实际发生额据实扣除外，还可以按照规定比例加计扣除。（　　）', '2', '1', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('168', '在外国成立且实际管理机构不在中国境内的企业，不是企业所得税的纳税义务人。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('169', '企业在汇总计算缴纳企业所得税时，其境外营业机构的亏损可以抵减境内营业机构的盈利。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('170', '对商品营销活动中，企业和单位对营销成绩突出的雇员以培训班、研讨会、工作考察等名义组织旅游活动，通过免收差旅费、旅游费对个人实行的营销业绩奖励，应根据所发生的费用作为劳务收入，按照“劳务报酬”项目征收个人所得税。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('171', '企业与其关联方之间的业务往来，不符合独立交易原则，或者企业实施其他不具有合理商业目的安排的，税务机关有权在该业务发生的纳税年度起5年内，进行纳税调整。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('172', '符合《国务院关于工人退休、退职的暂行办法》规定的退职条件并按该办法规定的退职费标准所领取的退职费，应在取得的当月按工资、薪金所得计算缴纳个人所得税。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('173', '个人通过非营利性的社会团体和国家机关向红十字事业的捐赠，在计算缴纳个人所得税时，准予按应纳税所得额的30%在税前扣除。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('174', '非居民企业在中国境内未设立机构、场所的，境外所得需在境内缴纳企业所得税。（　　）', '2', '0', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('175', '非居民企业转让财产所得，以收入全额减除财产净值后的余额为应纳税所得额。（　　）', '2', '1', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('176', '个人退职后6个月内又再次任职的，对个人已经缴纳个人所得税的退职费收入，不再与再次任职取得的工资薪金所得合并计算补缴个人所得税。（　　）', '2', '1', '4', '0', '27', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('177', '对于以房屋作为载体，不可以随意移动的附属设备和配套设施，无论在会计核算中是否单独记账与核算，都是无需要缴纳房产税的。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('178', '耕地占用税的纳税人为在我国境内占用耕地建房或者从事非农业建设的单位，不包括个人。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('179', '一票货物关税税额、进口环节增值税或者消费税税额在人民币100元以下的，可以免征关税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('180', '房屋交换，交换价格不相等的，由收取货币、实物、无形资产或其他经济利益的一方缴纳契税；交换价格相等的，免征契税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('181', '土地增值税纳税人转让房地产的，只有取得了货币收入，才需要缴纳土地增值税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('182', '对于用单据代替合同的，如果此项业务既书立合同，又开立单据的，合同与单据须同时贴花计税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('183', '扣缴义务人代扣代缴的资源税，应当向其机构所在地主管税务机关缴纳。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('184', '纳税人建造普通标准住宅出售，增值额未超过扣除项目金额20％的，予以免税；超过20％的，应按超过部分增值额缴纳土地增值税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('185', '纳税人新征用的非耕地，自批准征用当月起缴纳城镇土地使用税。（　　）', '2', '0', '4', '0', '28', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('186', '税务机关采取税收保全措施的期限一般不得超过6个月；重大案件需要延长的，应当报国家税务总局批准。（　　）', '2', '1', '4', '0', '29', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('187', '对纳税人采取税收保全措施，必须经税务机关向人民法院提出申请后，由人民法院执行。（　　）', '2', '0', '4', '0', '29', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('188', '纳税人以假报出口或者其他欺骗手段，骗取国家出口退税款的行为属于偷税行为。（　　）', '2', '0', '4', '0', '29', '1490925813', '0', '1');
INSERT INTO `ae_judgment` VALUES ('189', '根据《发票管理办法》的规定，填开发票的单位和个人应在发生经营业务确认营业收入时开具发票。未发生经营业务一律不准开具发票。（　　）', '2', '1', '4', '0', '29', '1490925813', '0', '1');
